var page = 1;
$(function(){
	$(document).on('click', '#view-more-leaders', function(){
		$.ajax({
			type: 'get',
			url: '/leaderboard',
			data: {page: ++page},
			success: function(data){
				$('#leaders').append(data);
				if($('#lead-num-1').length < 1){
					$('.lead-num').hide();
				}
			}
		});
	});
});